INSERT INTO `terms` (`id`, `name`, `description`, `is_deleted`, `created_user`, `updated_user`, `deleted_at`, `created_at`, `updated_at`) VALUES
(1, '3.1', 'Các tình trạng tồn tại trước mà không được khai báo và không được Công ty chấp nhận.', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(2, '3.2', 'Các tình trạng bẩm sinh và các khuyết tật khi sinh hoặc sự phát triển và sự tăng trưởng cơ thể bất thường hoặc những vấn đề về gen (bao gồm cả các chứng thoát vị cho tới 10 tuổi).', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(3, '3.3', 'Bệnh động kinh, tất cả các hình thức của tràn dịch não, tất cả các hình thức của hẹp bao quy đầu và các biến chứng của chúng, tất cả các trường hợp vẹo vách ngăn mũi.', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(4, '3.4', 'Viêm khớp xương, loãng xương, vẹo cột sống và tất cả các hình thức của bệnh thoái hóa xương.', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(5, '3.5', 'Phẫu thuật thẩm mỹ hoặc việc điều trị hay các biến chứng có liên quan đến việc làm đẹp bao gồm cả việc điều trị các vấn đề về da, viêm nang lông (trứng cá), rám da, tàn nhang, mụn thịt, thiếu sắc tố da, gàu.', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(6, '3.6', 'Giảm cân và tăng cân, các biến chứng về rụng tóc, sẹo, tiếp nhận hoặc sử dụng mỹ phẩm có tác dụng thuốc, vitamin, khoáng chất, sữa, chất dinh dưỡng bổ sung, các chương trình kiểm soát cân nặng và điều trị hoặc phẫu thuật lựa chọn.', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(7, '3.7', 'Phẫu thuật chỉnh hình, trừ khi bị thương tích do tai nạn và phẫu thuật chỉnh hình là cần thiết để khôi phục lại chức năng của Người được bảo hiểm.', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(8, '3.8', 'Các dịch vụ liên quan đến vô sinh, thai sản, sinh đẻ, sẩy thai, ph thai hoặc bất kỳ nguyên nhân nào có liên quan đến thai sản, việc triệt sản hoặc kiểm tra việc triệt sản.', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(9, '3.9', 'Các tình trạng có liên quan đến hội chứng suy giảm miễn dich mắc phải (AIDS) hoặc các bệnh lây truyền qua đường tình dục (STD).', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(10, '3.10', 'Điều trị hoặc phòng ngừa để giảm nhẹ các triệu chứng thông thường liên quan đến tuổi già, sự mãn kinh, loãng xương hoặc dậy thì sớm, loạn chức năng giới tính hoặc thay đổi giới tính.', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(11, '3.11', 'Kiểm tra sức khỏe, chăm sóc dưỡng bệnh bao gồm cả chữa bệnh bằng cách nghỉ ngơi và việc phục hồi.', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(12, '3.12', 'Bất kỳ sự điều trị, thuốc hoặc các đồ dùng y tế nào không liên quan đến chẩn đoán; và chẩn đoán không liên quan đến thương tích hoặc ốm đau hoặc không theo sự cần thiết về mặt y tế.', 0, 1, 8, NULL, '2020-01-20 02:21:21', '2020-02-07 01:59:12'),
(13, '3.13', 'Xét nghiệm bảng dị ứng cho sự không dung nạp thực phẩm, xét nghiệm các mức độ vitamin và các mức độ khoáng chất vi lượng.', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(14, '3.14', 'Tất cả các chi phí y tế liên quan đến sự bất thường của thị lực bao gồm nhưng không chỉ giới hạn ở tất cả các hình thức lác mắt (lé), kính đeo mắt, kính sát tròng, phẫu thuật LASIK và bất kỳ chi phí nào có liên quan đến việc điều trị trợ giúp thị giác.', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(15, '3.15', 'Bất kỳ sự điều trị nào để chẩn đoán, điều trị hoặc phòng ngừa một tình trạng nha khoa hoặc phẫu thuật chỉnh hình có liên quan đến răng, nướu hoặc hm trong trường hợp có liên quan đến răng, răng giả, cầu răng, điều trị ống chân răng, trám răng, chỉnh răng, lấy cao răng, nhổ răng và cấy implant, ngoại trừ thương tích do tai nạn đối với răng trong thời gian sản phẩm bảo hiểm bổ sung này đang có hiệu lực hoặc trừ khi được bảo hiểm theo quyền lợi chăm sóc răng của sản phẩm bảo hiểm bổ sung này. ', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(16, '3.16', 'Điều trị y tế có liên quan đến chứng nghiện rượu, sử dụng chất cĩ cồn, sử dụng và nghiện thuốc lá, ma túy hoặc các chất gây nghiện khác hoặc các chất tạo thành thói quen.', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(17, '3.17', 'Điều trị y tế có liên quan đến các rối loạn thần kinh, các rối loạn tâm thần, lo âu, các vấn đề về tâm thần, các rối loạn nhân cách, các rối loạn về nói, tự kỷ, stress, các rối loạn về ăn và các tình trạng rối loạn tăng động giảm chú ý (ADHD). ', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(18, '3.18', 'Điều trị y tế mà đang trong giai đoạn thử nghiệm hoặc đang trong sự phát triển thí nghiệm.', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(19, '3.19', 'Điều trị các rối loạn về ngủ, các rối loạn về thở có liên quan đến giấc ngủ bao gồm cả ngy v ngưng thở trong lúc ngủ.', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(20, '3.20', 'Bất kỳ sự tiêm chủng hoặc chủng ngừa nào, ngoại trừ vắc xin bệnh dại cần thiết sau khi bị động vật tấn công hoặc chích ngừa uốn ván cần thiết sau khi bị tai nạn hoặc bị thương tật.', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(21, '3.21', 'Điều trị không được xem là y học hiện đại, ngoại trừ y học thay thế được chi trả theo quyền lợi điều trị ngoại trú.', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(22, '3.22', 'Bất kỳ sự điều trị y tế nào do Bác sĩ y khoa thực hiện mà người này là cha mẹ, anh chị em ruột, vợ chồng hoặc con cái của Người được bảo hiểm. Người được bảo hiểm là Bác sĩ y khoa có đăng ký sẽ không được bồi hoàn cho các điều trị do tự mình thực hiện.', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(23, '3.23', 'Tự tử hoặc cố gắng tự tử, tự gây thương tích hoặc cố gắng tự gây thương tích cho dù là chính mình làm hoặc cho phép (những) người khác làm trong lúc có mất trí hay không. Điều này cũng bao gồm cả những tai nạn đối với Người được bảo hiểm do hít, sử dụng, uống, hoặc tiêm chích chất độc vào trong cơ thể hoặc sử dụng thuốc quá liều.', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(24, '3.24', 'Bất kỳ tổn thất hoặc thương tích nào phát sinh từ hành động của Người được bảo hiểm dưới ảnh hưởng của chất có cồn, thuốc gây nghiện, thuốc gây mê.', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(25, '3.25', 'Thương tật do các hành vi tham gia ẩu đả của Người được bảo hiểm, trừ khi chứng minh được đó là hành động phòng vệ chính đáng. ', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(26, '3.26', 'Thương tật trong khi Người được bảo hiểm phạm tội hoặc trong khi Người được bảo hiểm đang bị bắt giữ hoặc đang trốn thoát khỏi sự bắt giữ của cơ quan có thẩm quyền.', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(27, '3.27', 'Các thương tật do vi phạm luật giao thông.', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(28, '3.28', 'Thương tật trong khi Người được bảo hiểm tham gia thể thao chuyên nghiệp (bao gồm cả việc luyện tập cho môn thể thao đó), các môn thể thao hoặc các hoạt động nguy hiểm, và các môn thể thao tiếp xúc.', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(29, '3.29', 'Thương tật trong khi Người được bảo hiểm đi lên xuống hoặc di chuyển trên một máy bay không có giấy phép chuyên chở hành khách hoặc không hoạt động như là một máy bay thương mại.', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(30, '3.30', 'Thương tật trong khi Người được bảo hiểm đang lái máy bay hoặc đang làm việc trên máy bay như là một nhân viên của một công ty hàng không.', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(31, '3.31', 'Thương tật trong khi Người được bảo hiểm phục vụ như là một quân nhân, cảnh sát, hoặc một người tình nguyện và tham gia vào chiến tranh hoặc ngăn chặn tội phạm.', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(32, '3.32', 'Khủng bố, chiến tranh (cho dù có được tuyên bố hay không), sự xâm lược, các hành động của kẻ thù nước ngoài, nội chiến, cách mạng, nổi dậy, bạo động dân sự, dân chúng nổi dậy chống chính phủ, nổi loạn, đình công.', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(33, '3.33', 'Các bức xạ ion hóa hoặc nhiễm phóng xạ từ nhiên liệu hạt nhân hoặc từ chất thải hạt nhân từ sự đốt cháy nhiên liệu hạt nhân. Phóng xạ, độc hại, nổ hoặc tính chất nguy hiểm khác của bất kỳ sự tập hợp hạt nhân dễ nổ nào hoặc thành phần hạt nhân của nó.', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(34, '3.34', 'Điều trị y tế có liên quan đến việc nhiễm/ngộ độc trực tiếp hoặc gián tiếp do hóa chất hoặc thuốc.', 0, 1, 1, NULL, '2020-01-20 02:21:21', '2020-01-20 02:21:21'),
(35, '1.1', 'Người được bảo hiểm: là người được bảo hiểm của sản phẩm bảo hiểm bổ sung này, được ghi tên trong Giấy chứng nhận bảo hiểm hoặc các thỏa thuận sửa đổi bổ sung khác (nếu có). Người được bảo hiểm của sản phẩm bảo hiểm bổ sung này là Người được bảo hiểm của sản phẩm chính hoặc có thể là người khác nếu có quyền lợi có thể được bảo hiểm với Bên mua bảo hiểm và theo quy định của Dai-ichi Life Việt Nam tại từng thời điểm.', 0, 1, 1, NULL, '2020-01-21 10:34:03', '2020-01-21 10:34:34'),
(36, '1.2', 'Độ tuổi tham gia bảo hiểm: độ tuổi tham gia bảo hiểm của sản phẩm bảo hiểm bổ sung này là từ 0 đến 60 tuổi tính theo sinh nhật vừa qua của Người được bảo hiểm xét vào ngày bắt đầu hiệu lực của sản phẩm bảo hiểm bổ sung này. Tuổi tối đa khi kết thúc sản phẩm bảo hiểm bổ sung này là 65 tuổi.', 0, 1, 1, NULL, '2020-01-21 10:34:25', '2020-01-21 10:34:42'),
(37, '1.3', 'Thời hạn bảo hiểm: thời hạn bảo hiểm của sản phẩm bảo hiểm bổ sung này là 1 (một) năm và được gia hạn hàng năm nếu không có bên nào thông báo về việc chấm dứt hiệu lực của sản phẩm bảo hiểm bổ sung này; với điều kiện Bên mua bảo hiểm nộp đủ và đúng hạn các phí bảo hiểm đến hạn và chấp nhận các điều khoản và điều kiện của sản phẩm bảo hiểm bổ sung này.', 0, 1, 1, NULL, '2020-01-21 10:35:00', '2020-01-21 10:35:07'),
(38, '1.4', 'Ngày gia hạn năm hợp đồng: là ngày lặp lại hàng năm của ngày sản phẩm bảo hiểm bổ sung bắt đầu hiệu lực nếu không có bên nào thông báo về việc chấm dứt hiệu lực của sản phẩm bảo hiểm bổ sung này. Nếu năm không có ngày này, ngày cuối tháng tương ứng sẽ là Ngày gia hạn năm hợp đồng.', 0, 1, 1, NULL, '2020-01-21 10:35:31', '2020-01-21 10:35:31'),
(39, '1.5', 'Chương trình bảo hiểm: sản phẩm Chăm sóc sức khỏe gồm nhiều chương trình bảo hiểm do Dai-ichi Life Việt Nam quy định. Bên mua bảo hiểm sẽ lựa chọn Chương trình bảo hiểm cụ thể khi tham gia sản phẩm bảo hiểm bổ sung này. Chương trình bảo hiểm sẽ gồm quyền lợi điều trị nội trú và các quyền lựa chọn thêm như quyền lợi điều trị ngoại trú, quyền lợi nha khoa (nếu có). Chương trình bảo hiểm của từng Hợp đồng bảo hiểm sẽ được quy định trong Giấy chứng nhận bảo hiểm hoặc thỏa thuận sửa đổi bổ sung có liên quan khác (nếu có).', 0, 1, 1, NULL, '2020-01-21 10:46:30', '2020-01-21 10:46:30'),
(40, '1.6', 'Bảng liệt kê quyền lợi: là bảng chi tiết quyền lợi sản phẩm bảo hiểm Chăm sóc sức khỏe được đính kèm theo Hợp đồng bảo hiểm.', 0, 1, 1, NULL, '2020-01-21 10:46:54', '2020-01-21 10:46:54'),
(41, '1.7', 'Quyền lợi tối đa của một Bệnh tật/Thương tật: là tổng quyền lợi chi trả tối đa trong suốt thời gian tham gia sản phẩm bảo hiểm Chăm sóc sức khỏe cho tất cả các lần điều trị, nằm viện, phẫu thuật của các quyền lợi điều trị nội trú và ngoại trú cho cùng một Bệnh tật/Thương tật và các di chứng có liên quan đến Bệnh tật/Thương tật này. Quyền lợi tối đa của một Bệnh tật/Thương tật được quy định trong Bảng liệt kê quyền lợi sản phẩm bảo hiểm Chăm sóc sức khỏe theo Chương trình bảo hiểm quy định trong Giấy chứng nhận bảo hiểm hoặc thỏa thuận sửa đổi bổ sung có liên quan khác (nếu có).', 0, 1, 1, NULL, '2020-01-21 10:47:20', '2020-01-21 10:47:20'),
(42, '1.8', 'Phí bảo hiểm: là số tiền Bên mua bảo hiểm sẽ thanh toán cho Dai-ichi Life Việt Nam vào ngày đến hạn nộp phí. Phí bảo hiểm được tính toán dựa trên tuổi của Người được bảo hiểm, Chương trình bảo hiểm đã chọn và quyết định thẩm định của Dai-ichi Life Việt Nam tại thời điểm tham gia sản phẩm Chăm sóc sức khỏe hoặc vào các Ngày gia hạn năm hợp đồng.', 0, 1, 1, NULL, '2020-01-21 10:47:50', '2020-01-21 10:47:50'),
(43, '1.9', 'Mức phí bảo hiểm: là biểu phí của sản phẩm theo tuổi, quyền lợi điều trị nội trú và các quyền lợi lựa chọn thêm đã được Bộ Tài Chính phê duyệt.', 0, 1, 1, NULL, '2020-01-21 10:48:12', '2020-01-21 10:48:12'),
(44, '1.10', '1.10	Thời hạn loại trừ: là thời gian mà các sự kiện bảo hiểm của các quyền lợi điều trị nội trú và/hoặc ngoại trú do Bệnh tật xảy ra không được chi trả. Thời gian loại trừ này được áp dụng như sau: a.	Đối với quyền lợi điều trị nội trú: 30 ngày kể từ ngày Dai-ichi Life Việt Nam chấp thuận bảo hiểm sản phẩm bảo hiểm bổ sung này hoặc 10 ngày kể từ ngày Dai-ichi Life Việt Nam chấp nhận khôi phục hiệu lực gần nhất của sản phẩm bảo hiểm bổ sung này, tùy theo ngày nào xảy ra sau;  Đối với quyền lợi ngoại trú: 30 ngày kể từ ngày Dai-ichi Life Việt Nam chấp thuận bảo hiểm quyền lợi điều trị ngoại trú hoặc 10 ngày kể từ ngày Dai-ichi Life Việt Nam chấp nhận khôi phục hiệu lực gần nhất của sản phẩm bảo hiểm bổ sung này, tùy theo ngày nào xảy ra sau;  và: b.	1 năm kể từ ngày Dai-ichi Life Việt Nam chấp thuận bảo hiểm của năm hợp đồng đầu tiên của sản phẩm bảo hiểm bổ sung này cho những bệnh sau: •	U lành tính hay ác tính hoặc ung thư hoặc u nang •	Trĩ •	Bất kỳ thoát vị nào mà không phải là tình trạng bẩm sinh •	Mộng, hạt kết mạc góc mắt, đục thủy tinh thể •	Cắt amidan hoặc nạo VA •	Các loại sỏi  •	Giãn tĩnh mạch •	Lạc nội mạc tử cung •	Thay khớp (ngoại trừ gãy xương cấp tính) •	Các tình trạng của tuyến tiền liệt', 0, 1, 1, NULL, '2020-01-21 10:51:15', '2020-01-21 10:51:43'),
(45, '1.11', 'Tai nạn: là một hoặc một chuỗi sự kiện khách quan xảy ra do tác động của một lực, một vật bất ngờ từ bên ngoài lên cơ thể Người được bảo hiểm và gây ra Thương tật cho Người được bảo hiểm. Các sự kiện này chỉ được xem là tai nạn nếu hội đủ các điều kiện sau:  a.	Xảy ra do ngoài sự tiên liệu, kiểm soát cũng như ý muốn của Người được bảo hiểm;  b.	Phải là nguyên nhân trực tiếp, duy nhất gây ra Thương tật cho Người được bảo hiểm; c.	Không phải là những tổn thương thân thể do: đau ốm; bệnh tật; nhiễm các loại vi khuẩn hoặc vi-rút; ngộ độc; sử dụng trái phép các loại thuốc hoặc chất gây nghiện bất kỳ.', 0, 1, 1, NULL, '2020-01-21 10:52:35', '2020-01-21 10:52:42'),
(46, '1.12', 'Thương tật: là thương tích trên thân thể do tai nạn của Người được bảo hiểm do nguyên nhân trực tiếp và duy nhất là tác động từ bên ngoài gây ra, độc lập với những nguyên nhân khác và không phải do Người được bảo hiểm cố ý gây ra trong khi sản phẩm bảo hiểm bổ sung này đang có hiệu lực.', 0, 1, 1, NULL, '2020-01-21 10:54:42', '2020-01-21 10:54:42'),
(47, '1.13', 'Bệnh tật: là tình trạng sức khỏe kém hoặc tình trạng thể chất kém của Người được bảo hiểm so với tình trạng sức khỏe bình thường, do bệnh hoặc đau ốm gây ra trong khi sản phẩm bảo hiểm bổ sung này vẫn đang có hiệu lực; và được chẩn đoán bởi Bác sĩ có giấy phép hành nghề.', 0, 1, 1, NULL, '2020-01-21 11:05:25', '2020-01-21 11:05:25'),
(48, '1.14', 'Bệnh viện: là cơ sở khám chữa bệnh được thành lập và hoạt động theo luật pháp Việt Nam; được cấp giấy phép là một Bệnh viện, có chữ “Bệnh viện” trên con dấu và được phép lưu bệnh nhân qua đêm, bao gồm cả cơ sở phẫu thuật chuyên ngành.', 0, 1, 1, NULL, '2020-01-21 11:05:49', '2020-01-21 11:05:49'),
(49, '1.15', 'Phòng khám: là một cơ sở khám chữa bệnh được cấp giấy phép thành lập và hoạt động theo luật pháp Việt Nam và không có chức năng lưu bệnh nhân qua đêm.', 0, 1, 1, NULL, '2020-01-21 11:06:15', '2020-01-21 11:06:15'),
(50, '1.16', 'Bác sĩ: là người có bằng cấp chứng nhận và được phép hành nghề y học hiện đại (tây y) tại Việt Nam trong việc cung cấp dịch vụ điều trị y tế và phẫu thuật theo luật và các quy định có liên quan của Việt Nam.', 0, 1, 1, NULL, '2020-01-21 11:06:43', '2020-01-21 11:06:43'),
(51, '1.17', 'Nha sĩ: là người có bằng cấp chứng nhận và được phép hành nghề nha khoa tại Việt Nam theo luật và các quy định có liên quan của Việt Nam.', 0, 1, 1, NULL, '2020-01-21 11:07:08', '2020-01-21 11:07:08'),
(52, '1.18', 'Y tá/ điều dưỡng: là người có bằng cấp chứng nhận và được phép thực hiện các nhiệm vụ của y tá/ điều dưỡng tại Việt Nam.', 0, 1, 1, NULL, '2020-01-21 11:07:27', '2020-01-21 11:07:27'),
(53, '1.19', 'Bệnh nhân nội trú: là người được nhập viện theo yêu cầu của Bác sĩ của Bệnh viện và cần được lưu trú trong Bệnh viện (vì sự cần thiết về mặt y khoa) ít nhất 18 giờ để điều trị; bao gồm cả trường hợp bệnh nhân nội trú tử vong dưới 18 giờ sau khi nhập viện.', 0, 1, 1, NULL, '2020-01-21 11:07:59', '2020-01-21 11:07:59'),
(54, '1.20', 'Bệnh nhân ngoại trú: là người được điều trị Bệnh tật/Thương tật ở Phịng khm, khoa điều trị ngoại tr của Bệnh viện, hoặc phịng cấp cứu hoặc trải qua một phương thức điều trị m khơng cần thiết (về mặt y khoa) phải lưu trú trong Bệnh viện.', 0, 1, 1, NULL, '2020-01-21 11:08:25', '2020-01-21 11:08:25'),
(55, '1.21', 'Chuyên gia châm cứu: là người được cấp giấy phép để hành nghề theo lý thuyết y khoa phương đông truyền thống, chủ yếu là lồng những cây kim bằng kim loại xuyên qua da ở những điểm nhất định của cơ thể, để làm giảm sự đau đớn hoặc những triệu chứng khác gây nên những ốm đau nhất định.', 0, 1, 1, NULL, '2020-01-21 11:08:51', '2020-01-21 11:08:51'),
(56, '1.22', 'Chuyên gia nắn khớp xương: là chuyên gia được cấp giấy phép, chuyên về thao tác cấu trúc cơ xương với mục đích là khôi phục lại chức năng bình thường bằng việc điều chỉnh những vùng đang biểu lộ cấu trúc hoặc chức năng bất thường.', 0, 1, 1, NULL, '2020-01-21 11:09:19', '2020-01-21 11:09:19'),
(57, '1.23', 'Chuyên gia vật lý trị liệu: là chuyên gia được cấp giấy phép điều trị những rối loạn vật lý thông qua các kỹ thuật thao tác hoặc chuyển động và đề ra những bài tập tăng cường và những lời khuyên thích hợp.', 0, 1, 1, NULL, '2020-01-21 11:09:36', '2020-01-21 11:09:36'),
(58, '1.24', 'Sự cần thiết về mặt y khoa: là sự điều trị y khoa đáp ứng những điều kiện sau: a) 	theo đúng chẩn đoán, và điều trị cho Bệnh tật hoặc Thương tật đó; và b) 	theo đúng chỉ dẫn y khoa của y học hiện đại; và c) 	không phải chủ yếu vì sự tiện lợi của bệnh nhân hoặc gia đình của bệnh nhân, Bác sĩ điều trị; và d) 	theo đúng tiêu chuẩn được chấp nhận chung để chăm sóc cho các bệnh nhân, và được xem là thích hợp cho việc điều trị Bệnh tật hoặc Thương tật của bệnh nhân.', 0, 1, 1, NULL, '2020-01-21 11:17:28', '2020-01-21 11:17:28'),
(59, '1.25', 'Tình trạng tồn tại trước: là Bệnh tật/Thương tật của Người được bảo hiểm đã có trước ngày sản phẩm bảo hiểm bổ sung này có hiệu lực cho dù Người được bảo hiểm có nhận biết được tình trạng đó hay không.', 0, 1, 1, NULL, '2020-01-21 11:17:59', '2020-01-21 11:17:59'),
(60, '1.26', 'Chi phí y tế theo thông lệ/hợp lý: là chi phí cho việc chăm sóc sức khỏe phù hợp với phạm vi giá cả theo thông lệ và được chấp nhận hoặc các chi phí cho các dịch vụ giống nhau hoặc tương tự ở các Bệnh viện, cơ sở y tế, hoặc Phòng khám trên khắp cả nước Việt Nam.', 0, 1, 1, NULL, '2020-01-21 11:18:19', '2020-01-21 11:18:19'),
(61, '1.27', 'Điều trị nha khoa: là bất kỳ sự kiểm tra, điều trị và phẫu thuật liên quan đến những bệnh của răng và nướu hoặc hm trong trường hợp có liên quan đến răng; và được thực hiện bởi Nha sĩ.', 0, 1, 1, NULL, '2020-01-21 11:18:39', '2020-01-21 11:18:39'),
(62, '1.28', 'Y học thay thế: là các cách chữa bệnh khác, như là y học dược thảo truyền thống của Việt Nam hay Trung Quốc, và những cách tương tự mà không phải là y học hiện đại (tây y).', 0, 1, 1, NULL, '2020-01-21 11:18:56', '2020-01-21 11:18:56'),
(63, '1.29', 'Điều dưỡng tại nhà: là việc điều dưỡng chăm sóc tại nhà ngay sau khi Người được bảo hiểm xuất viện, được thực hiện bởi Y tá/điều dưỡng và phải có xác nhận của Bác sĩ điều trị là cần thiết về mặt y khoa.', 0, 1, 1, NULL, '2020-01-21 11:19:31', '2020-01-21 11:19:31'),
(64, '1.30', 'Mức miễn thường: là số tiền cố định đầu tiên của chi phí y tế hợp lệ cho mỗi năm hoặc mỗi lần khám bệnh hoặc mỗi bệnh tật mà Người được bảo hiểm có trách nhiệm thanh toán như được nêu trong Bảng liệt kê quyền lợi của sản phẩm bảo hiểm Chăm sóc sức khỏe này. Mức miễn thường vẫn được áp dụng ngay cả khi chi phí y tế đã được thanh toán một phần bởi đơn vị bảo hiểm khác.', 0, 1, 1, NULL, '2020-01-21 11:19:49', '2020-01-21 11:19:49'),
(65, '1.31', 'Khủng bố: là một hành động, bao gồm nhưng không chỉ giới hạn ở việc sử dụng vũ lực hoặc bạo lực và/hoặc sự đe dọa của bất kỳ người hoặc (các) nhóm người nào, cho dù hành động đơn lẻ hoặc thay mặt cho hoặc có liên quan đến bất kỳ (các) tổ chức hoặc (các) chính phủ nào, được thực hiện vì mục đích chính trị, tôn giáo, ý thức hệ hoặc các mục đích tương tự bao gồm cả ý định tác động đến bất kỳ chính phủ nào và/hoặc đặt dân chúng, hoặc bất kỳ bộ phận dân chúng nào trong sự sợ hãi.', 0, 1, 1, NULL, '2020-01-21 11:20:13', '2020-01-21 11:20:13'),
(66, '1.32', 'Tình trạng bẩm sinh, khuyết tật khi sinh: là một sự bất thường về mặt thể chất hoặc tinh thần có từ khi sinh ra (hoặc do di truyền hoặc do gen) cũng như những bất thường về mặt thể chất hoặc tinh thần phát triển sau đó do những nhân tố hoặc những tình trạng xuất hiện hoặc không xuất hiện vào lúc sinh ra, cho dù Người được bảo hiểm có nhận biết được tình trạng đó hay không.', 0, 1, 1, NULL, '2020-01-21 11:20:32', '2020-01-21 11:20:32'),
(67, '1.33', 'Thể thao tiếp xúc: là môn thể thao có sự tiếp xúc, va chạm cơ thể giữa những người chơi như là bóng rổ, bóng đá, bóng bầu dục, bóng chày …', 0, 1, 1, NULL, '2020-01-21 11:20:50', '2020-01-21 11:20:50'),
(68, '1.34', 'Thể thao hoặc các hoạt động nguy hiểm: là các môn thể thao hoặc các hoạt động bao gồm   nhưng không chỉ giới hạn ở săn bắn động vật, tất cả các hình thức đua (không phải bằng chân), đua xe go-kart, đua xe hơi đường trường và thử nghiệm xe hơi, trượt băng, trượt patin, trượt tuyết, trượt tuyết trên ván trượt, trượt ván, quyền anh, karate, võ thuật và môn đấu vật, nhảy dù (ngoại trừ những tình huống để bảo tồn mạng sống), đi lên hoặc di chuyển trên một khí cầu, bay lượn, nhảy bungee, lặn có bình dưỡng khí và thiết bị thở dưới nước …', 0, 1, 1, NULL, '2020-01-21 11:21:13', '2020-01-21 11:21:13'),
(69, '1.35', 'Phẫu thuật thẩm mỹ và/hoặc phẫu thuật chỉnh hình: là phẫu thuật được thực hiện chỉ để cải thiện hoặc nâng cao vẻ bề ngoài của Người được bảo hiểm thông qua các kỹ thuật giải phẫu và y khoa vì bất kỳ lý do nào.', 0, 1, 1, NULL, '2020-01-21 11:21:43', '2020-01-21 11:21:43'),
(70, '1.36', 'Tình trạng nguy cấp: là tình trạng sức khỏe của Người được bảo hiểm thay đổi đột ngột do Bệnh tật/Thương tật, đòi hỏi phải có sự điều trị y tế khẩn cấp và Bác sĩ chẩn đoán rằng nếu không có sự trợ giúp y tế khẩn cấp thì tình trạng Bệnh tật/Thương tật có thể đe dọa đến tính mạng của Người được bảo hiểm và/hoặc dẫn đến tử vong.', 0, 1, 1, NULL, '2020-01-21 11:22:12', '2020-01-21 11:22:12'),
(71, '1.37', 'Chi phí xe cấp cứu: là chi phí vận chuyển cấp cứu và chăm sóc trong quá trình vận chuyển Người được bảo hiểm đang trong Tình trạng nguy cấp tới Bệnh viện/Phòng khám gần nhất hoặc từ một Bệnh viện/Phịng khm đến một Bệnh viện khc trên xe cấp cứu của Bệnh viện/Phòng khám.', 0, 1, 1, NULL, '2020-01-21 11:22:33', '2020-01-21 11:22:33'),
(72, '1.38', 'Điều trị trước khi nhập viện: bao gồm việc khám kiểm tra, xét nghiệm, chẩn đoán hình ảnh theo yêu cầu của Bác sĩ và thuốc được kê đơn, có liên quan trực tiếp đến một tình trạng y tế cần phải nhập viện để điều trị theo chẩn đoán của Bác sĩ, được thực hiện trong vòng 30 ngày trước khi nhập viện.', 0, 1, 1, NULL, '2020-01-21 11:22:57', '2020-01-21 11:22:57'),
(73, '1.39', 'Điều trị sau khi xuất viện: là việc điều trị tiếp theo sau khi xuất viện theo chỉ định của Bác sĩ điều trị trong Bệnh viện, có liên quan trực tiếp đến việc điều trị nội trú trước đó, nhưng không vượt quá 60 ngày kể từ ngày xuất viện. Việc điều trị tiếp theo bao gồm khám kiểm tra, xét nghiệm, chẩn đoán hình ảnh theo yêu cầu của Bác sĩ, và thuốc được kê đơn.', 0, 1, 1, NULL, '2020-01-21 11:25:40', '2020-01-21 11:25:40'),
(74, '1.40', 'Phí thuần (chỉ áp dụng nếu sản phẩm bảo hiểm chính thuộc dòng sản phẩm bảo hiểm liên kết đầu tư): là khoản chi phí bảo hiểm rủi ro được khấu trừ hàng tháng để đảm bảo chi trả quyền lợi bảo hiểm theo Quy tắc và Điều khoản hợp đồng sản phẩm bảo hiểm bổ sung này. Mức chi phí bảo hiểm rủi ro được tính dựa theo độ tuổi, giới tính, tình trạng sức khỏe và nghề nghiệp của Người được bảo hiểm. Chi phí bảo hiểm rủi ro được điều chỉnh theo tuổi đạt được của Người được bảo hiểm và tương ứng với quyền lợi điều trị nội trú và các quyền lựa chọn thêm.', 0, 1, 1, NULL, '2020-01-21 11:26:03', '2020-01-21 11:26:03'),
(75, '2.1', 'Quyền lợi điều trị nội trú a.	Phạm vi bảo hiểm:  -	Trong thời gian hiệu lực của sản phẩm bảo hiểm bổ sung này và sau Thời hạn loại trừ được quy định tại Điều 1.10; và  -	Người được bảo hiểm điều trị Bệnh tật/Thương tật theo quyền lợi điều trị nội trú trong Bảng liệt kê quyền lợi, không thuộc những trường hợp loại trừ theo quy định tại Điều 3 và những trường hợp loại trừ theo quyết định thẩm định của Dai-ichi Life Việt Nam. b.	Quyền lợi bảo hiểm: Dai-ichi Life Việt Nam sẽ chi trả các chi phí y tế thực tế theo thông lệ và hợp lý cần thiết về mặt y khoa theo quy định chi tiết của quyền lợi điều trị nội trú trong Bảng liệt kê quyền lợi theo sự lựa chọn Chương trình bảo hiểm của sản phẩm bảo hiểm Chăm sóc sức khỏe được quy định trong Giấy chứng nhận bảo hiểm hoặc thỏa thuận sửa đổi bổ sung có liên quan khác (nếu có).   Số tiền chi trả của các hạng mục trong quyền lợi điều trị nội trú sẽ không vượt quá giới hạn chi trả tối đa của mỗi hạng mục của quyền lợi điều trị nội trú theo quy định trong Bảng liệt kê quyền lợi.  Trong mọi trường hợp, tổng số tiền chi trả cho quyền lợi điều trị nội trú của một Bệnh tật/Thương tật không vượt quá Quyền lợi tối đa của một Bệnh tật/Thương tật trong Bảng liệt kê quyền lợi theo sự lựa chọn Chương trình bảo hiểm của sản phẩm bảo hiểm Chăm sóc sức khỏe được quy định trong Giấy chứng nhận bảo hiểm hoặc thỏa thuận sửa đổi bổ sung có liên quan khác (nếu có).', 0, 1, 1, NULL, '2020-01-21 11:34:38', '2020-01-21 11:34:38'),
(76, '2.2', 'Quyền lợi điều trị ngoại trú (áp dụng khi Bên mua bảo hiểm có lựa chọn tham gia thêm) a.	Phạm vi bảo hiểm:  -	Trong thời gian hiệu lực của sản phẩm bảo hiểm bổ sung này và sau Thời hạn loại trừ được quy định tại Điều 1.10; và  -	Người được bảo hiểm điều trị ngoại trú Bệnh tật/Thương tật theo quyền lợi điều trị ngoại trú trong Bảng liệt kê quyền lợi, không thuộc những trường hợp loại trừ theo quy định tại Điều 3 và những trường hợp loại trừ theo quyết định thẩm định của Dai-ichi Life Việt Nam. b.	Quyền lợi bảo hiểm: Dai-ichi Life Việt Nam sẽ chi trả các chi phí y tế thực tế (sau khi trừ đi Mức miễn thường) theo thông lệ và hợp lý của điều trị ngoại trú theo quy định chi tiết của quyền lợi điều trị ngoại trú trong Bảng liệt kê quyền lợi theo sự lựa chọn Chương trình bảo hiểm của sản phẩm bảo hiểm Chăm sóc sức khỏe được quy định trong Giấy chứng nhận bảo hiểm hoặc thỏa thuận sửa đổi bổ sung có liên quan khác (nếu có).   Số tiền chi trả của các hạng mục trong quyền lợi điều trị ngoại trú sẽ không vượt quá giới hạn chi trả tối đa của mỗi hạng mục của quyền lợi điều trị ngoại trú theo quy định trong Bảng liệt kê quyền lợi. Trong mọi trường hợp, tổng số tiền chi trả cho quyền lợi điều trị ngoại trú không vượt quá giới hạn chi trả tối đa của quyền lợi điều trị ngoại trú trong Bảng liệt kê quyền lợi theo sự lựa chọn Chương trình bảo hiểm của sản phẩm bảo hiểm Chăm sóc sức khỏe được quy định trong Giấy chứng nhận bảo hiểm hoặc thỏa thuận sửa đổi bổ sung có liên quan khác (nếu có).', 0, 1, 1, NULL, '2020-01-21 11:36:46', '2020-01-21 11:36:46'),
(77, '2.3', 'Quyền lợi chăm sóc răng (áp dụng khi Bên mua bảo hiểm có lựa chọn tham gia thêm) Trong thời gian sản phẩm Chăm sóc sức khỏe có hiệu lực, nếu Người được bảo hiểm có điều trị nha khoa theo quy định tại Điều 1.27, Dai-ichi Life Việt Nam sẽ chi trả các chi phí y tế điều trị nha khoa (sau khi trừ đi Mức miễn thường) của Người được bảo hiểm.  Trong mọi trường hợp, tổng số tiền chi trả cho quyền lợi chăm sóc răng không vượt quá giới hạn chi trả tối đa của quyền lợi chăm sóc răng theo quy định trong Bảng liệt kê quyền lợi theo sự lựa chọn Chương trình bảo hiểm của sản phẩm bảo hiểm Chăm sóc sức khỏe được quy định trong Giấy chứng nhận bảo hiểm hoặc thỏa thuận sửa đổi bổ sung có liên quan khác (nếu có).  Việc kiểm tra răng, nướu tổng quát và chụp X-quang toàn bộ răng ngay khi tham gia quyền lợi chăm sóc răng là điều kiện bắt buộc cho quyền lợi chăm sóc răng này và kết quả sẽ được gởi về cho Dai-ichi Life Việt Nam. Dai-ichi Life Việt Nam sẽ loại trừ những tình trạng tồn tại trước được ghi nhận trong kết quả kiểm tra răng, nướu và hàm (có liên quan đến răng) này. Chi phí cho kiểm tra răng, nướu tổng quát và chụp X-Quang toàn bộ răng nói trên sẽ được bảo hiểm theo Quy tắc, Điều khoản của sản phẩm bảo hiểm bổ sung này.', 0, 1, 1, NULL, '2020-01-21 11:37:07', '2020-01-21 11:37:15'),
(78, '2.4', 'Quyền lợi bảo hiểm khi đáo hạn hợp đồng Sản phẩm bảo hiểm bổ sung này không có giá trị thanh toán khi đáo hạn hợp đồng.', 0, 1, 1, NULL, '2020-01-21 11:37:34', '2020-01-21 11:37:34'),
(79, '2.5', 'Trong mọi trường hợp, tổng số tiền chi trả cho quyền lợi điều trị nội trú theo Điều 2.1 v quyền lợi điều trị ngoại trú theo Điều 2.2 cho một Bệnh tật/Thương tật không vượt quá Quyền lợi tối đa của một Bệnh tật/Thương tật theo quy định trong Bảng liệt kê quyền lợi theo sự lựa chọn Chương trình bảo hiểm của sản phẩm bảo hiểm Chăm sóc sức khỏe được quy định trong Giấy chứng nhận bảo hiểm hoặc thỏa thuận sửa đổi bổ sung có liên quan khác (nếu có).', 0, 1, 1, NULL, '2020-01-21 11:37:56', '2020-01-21 11:37:56');
